import React from 'react';
import { Link } from 'react-router-dom';

function BlogPage(){
    return(
        <>
            <h1>BlogPage</h1>
           <ul>
           {blogdata.map(post => (
                <BlogLink post={post}/>
            ))}
           </ul>
        </>
    )
}

function BlogLink({post}){
    return(
        <li>
          <Link to={`/blog/${post.slug}`}>{post.title}</Link>
        </li>
    );
}

const blogdata=[];
blogdata.push({
    title: 'que es react ',
    slug:'que-es-react',
    content:'React es el mejor',
    author:'juandc'
})
blogdata.push({
    title: 'que es vue',
    slug:'que-es-vue',
    content:'vue es el mejor',
    author:'perez'
})
blogdata.push({
    title: 'que es angular ',
    slug:'que-es-agular',
    content:'agular es el mejor',
    author:'carlos'
})

export {BlogPage}
