import React from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import { Link, useNavigate, useParams } from 'react-router-dom';
import { useAuthr } from '../Login/peticion';



function Profile(){
    const [url, setUrl]= React.useState(true)
    const auth=useAuthr();
    const {slug} = useParams();
    const navigate= useNavigate();
    console.log("un profile",auth.user)
    function lectura(e){
             setUrl(e.target.outerText);
    }
    
    


    console.log(slug)
    return(
    <Container>
        <Row>
            <Col className=' col-3'>
            <ul class="list-group">
            <Link to='../profile/information'> <li onClick={lectura} name="information" class="list-group-item bg-dark text-light active">Information</li></Link>
            <Link to='../profile/change'><li onClick={lectura} class="list-group-item bg-dark text-light" >Change password</li></Link>
            <Link to='../profile/delete'><li onClick={lectura} class="list-group-item bg-dark text-light">Delete courses</li>  </Link>
            <Link to='../profile/drop'><li onClick={lectura} class="list-group-item bg-dark text-light">Drop out</li></Link>
           <li onClick={()=>{auth.logout()}} class="list-group-item bg-dark text-light">Logout</li>
            </ul>
            </Col> 

           {(url === 'Information' || url===true)? <Col>
            <table class="table ">
                <thead>
                    <tr>
                    <th scope="col">Nombre</th>
                    <td >{auth.user.name}</td>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                    <th scope="col">Apellido</th>
                    <td >{auth.user.lastName}</td>
                    </tr>
                    <tr>
                    <th scope="col">Email</th>
                    <td >{auth.user.email}</td>
                    </tr>
                </tbody>
                </table>
          
            </Col>
           :'' }
        </Row>
    </Container>
    )
}

export {Profile}
