    let ref= React.useRef();
    React.useEffect(() => {
        // üëáÔ∏è use a ref (best)
        const element2 = ref.current;
        console.log(element2);
    
        // üëáÔ∏è use document.querySelector()
        // should only be used when you can't set a ref prop on the element
        // (you don't have access to the element)
        const element = document.querySelector('#container');
        console.log(element);
      }, []);
  function almacen(e){
    setData({
        ...data,
        [e.target.name]:e.target.value
    })

   }
   console.log(data)
  function enviar(){


   }
    return(
        <>
        <Container className="text-center pt-3">
        <form onSubmit={enviar}>
          <label >Ingresa el Titulo del articulo</label>
          <input required onChange={almacen} className="form-control mb-3 mt-3" type="text" name="title" placeholder="Titulo del articulo"/>
          <label >Ingresa el nombre del autor o autores</label>
          <input required onChange={almacen} className="form-control mb-3 mt-3" type="text" name="autor" placeholder="Autor"/>
          <label >Ingresa la fecha de publicacion</label>
          <input required onChange={almacen} ref={ref} className="form-control mb-3 mt-3" ref={10} value={new Date().toLocaleDateString('en-us', { weekday:"long", year:"numeric", month:"short", day:"numeric"}) } type="text" name="date"/>
          <label >Ingresa tu articulo</label>
          <textarea  name="textarea" rows="10" cols="50" required onChange={almacen} className="form-control mb-3 mt-3" type="text" placeholder="Escribe tu articulo aqu√≠"/>
       </form> 
       </Container>
        </>
        )
 
}

export {Enviar}
