import { Container } from "react-bootstrap"
import React from 'react'

function Enviar(){
    const [data, setData]=React.useState('')
    let ref= React.useRef();
    let n
    let arr=[];
    for(let i=0; i<n; i--){
      arr[i]=n-1
    }
    console.log(arr)


    React.useEffect(() => {
        // üëáÔ∏è use a ref (best)
        const element2 = ref.current;
        setData({date:element2.value});
      }, []);
  function almacen(e){
    setData({
        ...data,
        [e.target.name]:e.target.value
    })

   }
   console.log(data)
  function enviar(){
    function Articulo(){
      return (
        <>
        <label >Ingresa tu articulo</label>
          <textarea  name="textarea" rows="10" cols="50" required onChange={almacen} className="form-control mb-3 mt-3" type="text" placeholder="Escribe tu articulo aqu√≠"/>
      </>
      )
    }
    function Img(){
      return (
        <>
        <label >Ingresa una imagen url</label>
          <input required onChange={almacen} className="form-control mb-3 mt-3" type="text" name="imagen" placeholder="Titulo del articulo"/>
      </>
      )
    }
    

   }
    return(
        <>
        <Container className="text-center pt-3">
        <form onSubmit={enviar}>
          <label >Ingresa el Titulo del articulo</label>
          <input required onChange={almacen} className="form-control mb-3 mt-3" type="text" name="title" placeholder="Titulo del articulo"/>
          <label >Ingresa el nombre del autor o autores</label>
          <input required onChange={almacen} className="form-control mb-3 mt-3" type="text" name="autor" placeholder="Autor"/>
          <label >Ingresa la fecha de publicacion</label>
          <input required onChange={almacen} ref={ref} className="form-control mb-3 mt-3"  value={new Date().toLocaleDateString('en-us', { weekday:"long", year:"numeric", month:"short", day:"numeric"}) } type="text" name="date"/>
          <label >Ingresa tu articulo</label>
          <textarea  name="textarea" rows="10" cols="50" required onChange={almacen} className="form-control mb-3 mt-3" type="text" placeholder="Escribe tu articulo aqu√≠"/>
          <label >Ingresa una imagen url</label>
          <input required onChange={almacen} className="form-control mb-3 mt-3" type="text" name="imagen" placeholder="Titulo del articulo"/>
          {}
       </form> 
       </Container>
        </>
        )
 
}

export {Enviar}
