import { Container, Row, Col } from "react-bootstrap"
import React from 'react'
import { useAuth } from "../Menu/BlogData"
import { Link, useNavigate } from "react-router-dom";


function Enviar(){
  const navigate=useNavigate();
  const auth=useAuth();
  const [datos, setDatos]=React.useState();
  const [data, setData]=React.useState({slug:'10',id:'1'})
  const [data1, setData1]=React.useState({slug:'10',id:'1'})
  const [n, setn]=React.useState(0)
  const [states, setState]=React.useState(false);
  const [edit, setEdit]=React.useState(false);

  let url ='https://api.castelancarpinteyro.com/articles';
  async function leer(info) {
    /*  const response = */ await fetch(url, {
          method: 'POST', // or 'PUT'
          body: JSON.stringify(info), // data can be `string` or {object}!
          headers: {
          'Content-Type': 'application/json'
          }
})
  }

  React.useEffect( ()=>{
      fetch('https://api.castelancarpinteyro.com/articles')
          .then(response =>  response.json())
          .then(data =>{ 
              try{
                  let a=data.body[0].author
                  setDatos(data.body)
              }catch{

              }})
  },[])

console.log(datos)
  
    let ref= React.useRef();
    let arr=[];

  
    if(n>=0 && n<=5){
        for(let i=0; i<n; i++){
      arr[i]=i
    }
    }
  
console.log(data)
    React.useEffect(() => {
        // üëáÔ∏è use a ref (best)
        const element2 = ref.current;
        console.log(element2)
        setData({...data,date:element2.value});
      }, []);
  function almacen(e){
    setData({
       
        ...data,
        [e.target.name]:e.target.value,
    })

   }
  console.log(auth.blogdata.length-1)
  function enviar(e){
    e.preventDefault()
    data.slug=data.title.split(" ").join("-") 
    leer(data)
    data.id=(auth.blogdata.length)
    console.log(data)
    auth.blogdata.push(data)
    navigate('../aprende/articulos')
    }

    function selection(e){
      for(let i=0; i<datos.length; i++)
      if(datos[i].title == e.target.value){
        setEdit(datos[i]);
      }
      console.log(edit)
    }
       let ref1= React.useRef();
      let ref2= React.useRef();
      let ref3= React.useRef();
      let ref4= React.useRef();
      let ref5= React.useRef();
    function editar(id){
      const element1 = ref1.current;
      const element2 = ref2.current;
      const element3 = ref3.current;
      const element4 = ref4.current;
      const element5 = ref5.current;

   console.log(element1,element2,element3, element4, element5)

   setData1({
       
    ...data1,
    title:element1.value,
    author:element2.value,
    date:element3.value,
    content:element4.value,
    imagen:element5.value
})
console.log("holangas", data1)

      async function actualizar(id, info) {
       const url = `https://api.castelancarpinteyro.com/articles/${id}`;
     
       const response = await fetch(url, {
         method: 'PATCH',
         body: JSON.stringify(info),
         headers: {
           'Content-Type': 'application/json'
         }
       });
     
       const data = await response.json();
     
       return data;
     }
     actualizar(id, data)
 
     }

    return(
        <>
        <Container className="text-center pt-3">
          <Row>
          <Col className="col-3 pt-5 ">
          <label className="text-success pb-2">Numero de campos a agregar</label><br/>
         <input type='number' min='0' max="5" onChange={(e)=>{setn(parseInt(e.target.value))}} placeholder="num"/><br/>
         <label className="pb-4 pt-2">Rango entre 0 a 5 </label>
         <label>Por cada numero que agregue sale un campo de texto y otro de imagen, si no desea colocar una imagen puede
          dejarlo en blanco 
         </label>
        
          </Col>
         <Col><button onClick={()=>{setState(true); setn(5)}  } className='mt-4  mb-3 btn btn-primary shadow d-block w-100 mb-4'>Click si quieres editar un articulo</button>
        
      {states ?<> <label className='mt-4  mb-3 text-success'>Editar articulos publicados</label>
         <select onChange={selection}>
          {datos?datos.map(articles => (
            <>
             <option> {articles.title}</option></>
          )):''}  

          </select>
          </>:''}

        {(states && datos && edit)? <form onSubmit={(e)=>{
          e.preventDefault()
            editar(edit._id)
        }}> 
        
        <label className="mt-5">Ingresa el Titulo del articulo</label>
        <input ref={ref1} onChange={almacen} defaultValue={edit.title} className="form-control mb-3 mt-3" type="text" name="title" placeholder="Titulo del articulo"/>
        <label >Ingresa el nombre del autor o autores</label>
        <input ref={ref2} required onChange={almacen} defaultValue={edit.author} className="form-control mb-3 mt-3" type="text" name="author" placeholder="author"/>
        <label >Ingresa la fecha de publicacion</label>
        <input required  ref={ref3} className="form-control mb-3 mt-3"   type="text" name="date" readOnly/>
        <label >Ingresa tu articulo</label>
        <input  ref={ref4} defaultValue={edit.content} name="content" rows="10" cols="50" required onChange={almacen} className="form-control mb-3 mt-3" type="text" placeholder="Escribe tu articulo aqu√≠"/>
        <label >Ingresa una imagen url</label>
        <input  ref={ref5} required defaultValue={edit.imagen} onChange={almacen} className="form-control mb-3 mt-3" type="text" name="imagen" placeholder="Ingresa una url de imagen"/>

        
        {arr.map(numero=>(
          <>
           <label >Ingresa tu articulo {numero}</label>
           <textarea defaultValue={edit.content1} name={`content${numero+1}`} rows="10" cols="50" onChange={almacen} className="form-control mb-3 mt-3" type="text" placeholder="Escribe tu articulo aqu√≠"/>
           <label >Ingresa una imagen url {numero}</label>
          <input defaultValue={edit.image1}  onChange={almacen} className="form-control mb-3 mt-3" type="text" name={`image${numero+1}`} placeholder="Titulo del articulo"/>
        </>
        ))}
