import React from 'react';
import { Container } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import { Footer } from '../Componentes/Footer';
import { Nav } from '../Componentes/Nav';
//import { blogdata } from './BlogData';
import { useAuth } from './BlogData';

function BlogPost(){
    const auth= useAuth()
    const {slug} = useParams();

    const blogpost = auth.blogdata.find(post => post.slug ===slug);

    let content1=false;
    let image1=false;
    let content2=false;
    let image2=false;
    let content3=false;
    let image3=false;
    let content4=false;
    let image4=false;
    let content5=false;
    let image5=false;

    try{
        content1=  blogpost.content1;
        image1= blogpost.image1;
        content2=  blogpost.content2;
        image2= blogpost.image2;
        content3=  blogpost.content3;
        image3= blogpost.image3;
        content4=  blogpost.content14;
        image4= blogpost.image4;
        content5=  blogpost.content5;
        image5= blogpost.image5;

    }catch{
        console.log("error")
    }

    return(
        
        <>
        <Container className='text-center pb-2'>
            <h5 className='text-success'>{blogpost.date}</h5>
            <h2>{blogpost.title}</h2>
            <h5 className='pb-4'>{blogpost.author}</h5>
            <h5  className='pb-4'>{blogpost.content}</h5>
            <img className='pb-4' src={blogpost.imagen}/>

            {/* DINAMICAS */}
          
         { (content1)? <h5  className='pb-4'>{content1}</h5>:''}
         { (image1)?  <img className='pb-4' src={image1}/>: ''}

         { (content2)? <h5  className='pb-4'>{content2}</h5>:''}
         { (image2)?  <img className='pb-4' src={image2}/>: ''}
         
         { (content3)? <h5  className='pb-4'>{content3}</h5>:''}
         { (image3)?  <img className='pb-4' src={image3}/>: ''}

         { (content4)? <h5  className='pb-4'>{content4}</h5>:''}
         { (image4)?  <img className='pb-4' src={image4}/>: ''}

         { (content5)? <h5  className='pb-4'>{content5}</h5>:''}
         { (image5)?  <img className='pb-4' src={image5}/>: ''}
            

          
        </Container> 
        
         </>
    )
}




export {BlogPost}
